const RandomBytesGeneratorFunction = require('./RandomBytesGeneratorFunction')

it('should produce a predictable variable length random output', () => {
  const fn = new RandomBytesGeneratorFunction()

  expect(fn(1)).toMatchInlineSnapshot(`"0xa"`)
  expect(fn(50)).toMatchInlineSnapshot(
    `"0x47e7a20867f04c007c2b76e96ec49bf8d9398bd8930e119217"`,
  )
  expect(fn(1000)).toMatchInlineSnapshot(
    `""`,
  )
  expect(fn(2)).toMatchInlineSnapshot(`"0x11"`)
  expect(fn(20)).toMatchInlineSnapshot(`"0x2199c041141afe9e4de9"`)
})
